function doPost(e) {
  const sheet = SpreadsheetApp.getActiveSheet();
  const data = JSON.parse(e.postData.contents);

  // Adicionar cabeçalhos se for a primeira linha
  if (sheet.getLastRow() === 0) {
    const headers = ['Timestamp', 'Título do Formulário'];
    Object.keys(data.data || {}).forEach(key => headers.push(key));
    sheet.getRange(1, 1, 1, headers.length).setValues([headers]);
  }

  // Adicionar dados do formulário como nova linha
  const row = [
    new Date(data.submitted_at),
    data.form_title || 'Formulário Sem Título'
  ];

  Object.values(data.data || {}).forEach(value => row.push(value));
  sheet.appendRow(row);

  return ContentService.createTextOutput('Sucesso');
}
